(122, 1, '2023-03-21 09:36:54', '2023-03-21 06:36:54', '<!-- wp:group {\"align\":\"full\",\"style\":{\"color\":{\"background\":\"#f8f4e4\"}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignfull has-background\" style=\"background-color:#f8f4e4\"><!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":6,\"style\":{\"color\":{\"text\":\"#000000\"}}} -->\n<h6 class=\"has-text-color\" id=\"ecosystem\" style=\"color:#000000\">ВСЕ О ГОСТИНИЦЕ</h6>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"lineHeight\":\"1.1\",\"fontSize\":\"4.8vw\"},\"color\":{\"text\":\"#bd6617\"}}} -->\n<p class=\"has-text-color\" style=\"color:#bd6617;font-size:4.8vw;line-height:1.1\"><strong>Номера в нашей гостинице</strong></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns {\"align\":\"full\",\"className\":\"is-style-twentytwentyone-columns-overlap\"} -->\n<div class=\"wp-block-columns alignfull is-style-twentytwentyone-columns-overlap\"><!-- wp:column {\"width\":\"90%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:90%\"><!-- wp:shortcode -->\n[smartslider3 slider=\"2\"]\n<!-- /wp:shortcode --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"width\":\"33.38%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:33.38%\"><!-- wp:paragraph {\"textColor\":\"black\",\"gradient\":\"red-to-purple\"} -->\n<p class=\"has-black-color has-red-to-purple-gradient-background has-text-color has-background\"><strong>Гостиница \"HotHotels\"</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"style\":{\"color\":{\"text\":\"#000000\"}},\"fontSize\":\"extra-small\"} -->\n<p class=\"has-text-color has-extra-small-font-size\" style=\"color:#000000\">Гостиница начала свое существование еще в 80-х годах 20 века. Сюда приезжали лучшие скульпторы мира из Италии и Франции. Мебель сюда везли со всего мира. Когда СССР распался, то гостиница понесла большие убытки и потеряла популярность. Всю мебель начали распродавать, чтобы сохранить ее местоположение в центре города. Теперь эта гостиница имеет просто название от того, что было раньше, но и по сей день, люди останавливаются в ней, чтобы прочувствовать эту историю.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"align\":\"right\",\"id\":51,\"width\":422,\"height\":262,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"is-style-rounded\"} -->\n<figure class=\"wp-block-image alignright size-large is-resized is-style-rounded\"><img src=\"http://mysite2.rus/wp-content/uploads/2023/03/Стандарт2-1024x638.jpg\" alt=\"\" class=\"wp-image-51\" width=\"422\" height=\"262\"/><figcaption class=\"wp-element-caption\">Стандарт-класс</figcaption></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"33%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:33%\"><!-- wp:image {\"id\":48,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"is-style-rounded\"} -->\n<figure class=\"wp-block-image size-large is-style-rounded\"><img src=\"http://mysite2.rus/wp-content/uploads/2023/03/Эконом-1-комната-3-1024x703.jpg\" alt=\"\" class=\"wp-image-48\"/><figcaption class=\"wp-element-caption\">Эконом-класс</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:spacer -->\n<div style=\"height:100px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:spacer {\"height\":\"0px\"} -->\n<div style=\"height:0px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:spacer {\"height\":\"0px\"} -->\n<div style=\"height:0px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:image {\"id\":76,\"width\":382,\"height\":254,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"is-style-rounded\"} -->\n<figure class=\"wp-block-image size-large is-resized is-style-rounded\"><img src=\"http://mysite2.rus/wp-content/uploads/2023/03/Люкс1-1-1024x683.jpg\" alt=\"\" class=\"wp-image-76\" width=\"382\" height=\"254\"/><figcaption class=\"wp-element-caption\">Люкс класс</figcaption></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"33.62%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:33.62%\"><!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":49,\"width\":390,\"height\":260,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"is-style-rounded\"} -->\n<figure class=\"wp-block-image size-large is-resized is-style-rounded\"><img src=\"http://mysite2.rus/wp-content/uploads/2023/03/Эконом-люкс-1024x682.jpg\" alt=\"\" class=\"wp-image-49\" width=\"390\" height=\"260\"/><figcaption class=\"wp-element-caption\">Эконом-люкс класс</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:spacer -->\n<div style=\"height:100px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:image {\"align\":\"full\",\"id\":50,\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"className\":\"is-style-rounded\"} -->\n<figure class=\"wp-block-image alignfull size-full is-style-rounded\"><img src=\"http://mysite2.rus/wp-content/uploads/2023/03/Стандарт-обычный.jpg\" alt=\"\" class=\"wp-image-50\"/><figcaption class=\"wp-element-caption\">Стандарт-комфорт класс</figcaption></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:spacer -->\n<div style=\"height:100px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:group -->\n\n<!-- wp:image {\"align\":\"center\",\"id\":54,\"width\":268,\"height\":213,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image aligncenter size-full is-resized\"><img src=\"http://mysite2.rus/wp-content/uploads/2023/03/Логотип_метро-transformed.png\" alt=\"\" class=\"wp-image-54\" width=\"268\" height=\"213\"/><figcaption class=\"wp-element-caption\">Гостиница находится в шаговой доступности от метро(Адмиралтейская, Невский проспект/Гостиный двор)</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:social-links -->\n<ul class=\"wp-block-social-links\"><!-- wp:social-link {\"url\":\"https://vk.com/\",\"service\":\"vk\"} /-->\n\n<!-- wp:social-link {\"url\":\"https://web.whatsapp.com/\",\"service\":\"whatsapp\"} /-->\n\n<!-- wp:social-link {\"url\":\"telegram.org\",\"service\":\"telegram\"} /--></ul>\n<!-- /wp:social-links -->', 'HOT HOTELS', '', 'inherit', 'closed', 'closed', '', '5-revision-v1', '', '', '2023-03-21 09:36:54', '2023-03-21 06:36:54', '', 5, 'http://mysite2.rus/?p=122', 0, 'revision', '', 0),
