(207, 1, '2023-03-22 10:56:43', '2023-03-22 07:56:43', '<!-- wp:group {\"align\":\"full\",\"style\":{\"color\":{\"background\":\"#f8f4e4\"}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignfull has-background\" style=\"background-color:#f8f4e4\"><!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":6,\"style\":{\"color\":{\"text\":\"#000000\"}}} -->\n<h6 class=\"has-text-color\" id=\"ecosystem\" style=\"color:#000000\">ВСЕ О ГОСТИНИЦЕ</h6>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"lineHeight\":\"1.1\",\"fontSize\":\"4.8vw\"},\"color\":{\"text\":\"#bd6617\"}}} -->\n<p class=\"has-text-color\" style=\"color:#bd6617;font-size:4.8vw;line-height:1.1\"><strong>Классы номеров в нашей гостинице</strong></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns {\"align\":\"full\",\"className\":\"is-style-twentytwentyone-columns-overlap\"} -->\n<div class=\"wp-block-columns alignfull is-style-twentytwentyone-columns-overlap\"><!-- wp:column {\"width\":\"90%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:90%\"><!-- wp:shortcode -->\n[smartslider3 slider=\"2\"]\n<!-- /wp:shortcode --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"width\":\"33.38%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:33.38%\"><!-- wp:paragraph {\"textColor\":\"black\",\"gradient\":\"red-to-purple\"} -->\n<p class=\"has-black-color has-red-to-purple-gradient-background has-text-color has-background\"><strong>Гостиница \"HotHotels\"</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"style\":{\"color\":{\"text\":\"#000000\"}},\"fontSize\":\"extra-small\"} -->\n<p class=\"has-text-color has-extra-small-font-size\" style=\"color:#000000\">Гостиница начала свое существование еще в 80-х годах 20 века. Сюда приезжали лучшие скульпторы мира из Италии и Франции. Мебель сюда везли со всего мира. Когда СССР распался, то гостиница понесла большие убытки и потеряла популярность. Всю мебель начали распродавать, чтобы сохранить ее местоположение в центре города. Теперь эта гостиница имеет просто название от того, что было раньше, но и по сей день, люди останавливаются в ней, чтобы прочувствовать эту историю.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"align\":\"right\",\"id\":51,\"width\":404,\"height\":251,\"sizeSlug\":\"large\",\"linkDestination\":\"custom\",\"className\":\"is-style-rounded\"} -->\n<figure class=\"wp-block-image alignright size-large is-resized is-style-rounded\"><a href=\"http://mysite2.rus/2023/03/22/%d1%81%d1%82%d0%b0%d0%bd%d0%b4%d0%b0%d1%80%d1%82-%d0%ba%d0%bb%d0%b0%d1%81%d1%81/\"><img src=\"http://mysite2.rus/wp-content/uploads/2023/03/Стандарт2-1024x638.jpg\" alt=\"\" class=\"wp-image-51\" width=\"404\" height=\"251\"/></a><figcaption class=\"wp-element-caption\">Стандарт-класс</figcaption></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"33%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:33%\"><!-- wp:image {\"id\":48,\"sizeSlug\":\"large\",\"linkDestination\":\"custom\",\"className\":\"is-style-rounded\"} -->\n<figure class=\"wp-block-image size-large is-style-rounded\"><a href=\"http://mysite2.rus/2023/03/22/fiosrfjri/\"><img src=\"http://mysite2.rus/wp-content/uploads/2023/03/Эконом-1-комната-3-1024x703.jpg\" alt=\"\" class=\"wp-image-48\"/></a><figcaption class=\"wp-element-caption\">Эконом-класс</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:spacer -->\n<div style=\"height:100px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:spacer {\"height\":\"0px\"} -->\n<div style=\"height:0px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:spacer {\"height\":\"0px\"} -->\